// Get the first item from the previous node
const input = items[0].json;

// Initialize an empty array to store the new items
let newItems = [];

// Loop through each key in the input object
for (const key in input) {
  // Create a new item for each key-value pair
  newItems.push({ json: { [key]: input[key] } });
}

// Return the new items array
return newItems;

parameters.inputSchema:
{
  "$id": "https://example.com/section.schema.json",
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Section Content Schema",
  "type": "object",
  "properties": {
    "section_metatitle": {
      "type": "string",
      "description": "The metatitle of the section."
    },
    "section_content": {
      "description": "The content of the section, which can be a string, an array of strings, or an object containing FAQs.",
      "anyOf": [
        { "$ref": "#/$defs/stringContent" },
        { "$ref": "#/$defs/arrayContent" },
        { "$ref": "#/$defs/faqContent" }
      ]
    }
  },
  "required": ["section_metatitle", "section_content"],
  "additionalProperties": true,
  "$defs": {
    "stringContent": {
      "type": "string",
      "description": "The content of the section as a single string."
    },
    "arrayContent": {
      "type": "array",
      "description": "The content of the section as an array of strings.",
      "items": {
        "type": "string"
      }
    },
    "faqContent": {
      "type": "object",
      "description": "The content of the section as an object containing FAQs.",
      "properties": {
        "faqItems": {
          "type": "array",
          "description": "A list of FAQ items.",
          "items": {
            "type": "object",
            "properties": {
              "question": {
                "type": "string",
                "description": "The FAQ question."
              },
              "answer": {
                "type": "string",
                "description": "The FAQ answer."
              }
            },
            "required": ["question", "answer"]
          }
        }
      },
      "required": ["faqItems"]
    }
  }
}

